import React, { useState } from 'react'
import './App.css';

const App = () => {
  const usersData = [

  ]

  const [users, setUsers] = useState(usersData)

  const addUser = user => {
		user.id = users.length 
		setUsers([ ...users, user ])
	}
	const deleteUser = id => {
		setUsers(users.filter(user => user.id !== id))
	}

  return (
    <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <AddUserForm addUser={addUser} />
        </div><br></br>
        <div className="flex-large">
          <UserTable users={users} deleteUser={deleteUser} />
        </div>
      </div>
    </div>
  )
}

const AddUserForm = props => {
	const initialFormState = { id: null, nom: '', Prenom: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!user.nom || !user.prenom) return

				props.addUser(user)
				setUser(initialFormState)
			}} action="http://localhost:3000/list" method="POST"
		>
			<label id="nom">Nom &nbsp;&nbsp;</label>
			<input type="text" name="nom" value={user.nom} onChange={handleInputChange} />
			<label id="nom">&nbsp;&nbsp;Pr√©nom &nbsp;&nbsp;</label>
			<input type="text" name="Prenom" value={user.prenom} onChange={handleInputChange} />&nbsp;&nbsp;
			&nbsp;&nbsp;<button class="btn btn-primary">Ajouter</button>
		</form>

	)
}	

export default App
