import React, { Component } from 'react';


 class List extends Component {

  constructor(props) {
    super(props);

    this.state = {
      resAPI: ""
    };
  }



  componentWillMount() {
    fetch('http://localhost:3000/list')
      .then(res => res.text())
      .then(data => this.setState({ resAPI: data }));
  }


  render() {
    return (
      <div className="contact">
        <h1>Liste</h1>
        <button  id="boutton1" onClick={()=>{
            var list = this.state.resAPI
            var lister = JSON.parse(list)

            var pousser = []
          for(var i=0;i<lister.length;i++)
          {
            document.getElementById("name").innerHTML += "<div id='spannom'><span > Nom : "+lister[i].nom+"</span><br></div>";
            document.getElementById("name").innerHTML += "<div id='spanprenom'><span > Prénom : "+lister[i].Prenom+ "</span><br></div></div>";
            pousser.push(lister.length)
          }}
            }>
            LISTER
        </button>
          <div id="name"></div>
          <div id="map">


            {this.state.length > 0 ? (
				props.users.map(user => (
					<tr key={user.id}>
                        <td>{user.id}</td>
						<td>{user.name}</td>
						<td>{user.username}</td>
						<td>
						<button className="btn btn-danger" onClick={() => {props.deleteUser(user.id)}}>X</button>
						</td>
					</tr>
				))
			) : (
				<tr>	
				</tr>
			)}

          </div>
      </div>
    )
  }
}

export default List;
