import React, { Component } from 'react';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css'; 
import Ajouter from './ajouter'


 class List extends Component {

constructor(props){
  super(props);
  this.state = {
    list: []
  }
}

// Récupère la liste 
componentDidMount() {
  this.getList();
}

// Récupère la liste des éléments de l'application Express
getList = () => {
  fetch('http://localhost:8080/list')
  .then(res => res.json())
  .then(list => this.setState({ list }))
}

render() {
  const { list } = this.state;

  return (
    <div className="App">

        <Ajouter/>
      <br/>
      <table>
        <thead>
          <tr>
          <th id="listedonne">Id</th>
          <th id="listedonne">Nom</th>
          <th id="listedonne">Prenom</th>
          <th id="listedonne">Action</th>
          </tr>
        </thead>
        <tbody>
      {/*Vérifiez si des objets sont trouvés */}
      {list.length ? (
        <tr>
          {/* Rendre la liste des articles */}
          {list.map((item) => {
            return(
              <div id="name">

                  <td id="listedonne">{item.nom}</td>
                  <td id="listedonne">{item.Prenom}</td>
                  <td id="listedonne"><button onClick={()=>{
                    confirmAlert({
                      customUI: ({ onClose }) => {
                        return (
                          <div className='custom-ui'>
                           <form method="POST" action="http://localhost:8080/list?_method=PUT" enctype="application/x-www-form-urlencoded">
                              <input type="hidden" name="_method" value="PUT"/>
                              <input type="text" name="nom" placeholder={item.nom}/><br/><br/>
                              <input type="text" name="Prenom" placeholder={item.Prenom}/><br/><br/>
                              <input type="hidden" name="id" value={item.id}/>
                            <button onClick={onClose}>Cancel</button>
                            <button>
                              Edit
                            </button>
                            </form>
                          </div>
                        );
                      }
                    })
                }
                }>modifier</button>

                <button onClick = {()=>{
                    confirmAlert({
                     customUI: ({ onClose }) => {
                       return (
                         <div className='custom-ui'>
                           <form method="POST" action="http://localhost:8080/list?_method=DELETE" enctype="application/x-www-form-urlencoded">
                             <input type="hidden" name="_method" value="DELETE"/>

                             <input type="hidden" name="id" value={item.id}/>
                             <span>confirmer supression<br/>{item.nom + ' '+item.Prenom}</span>
                             <br/><br/>
                           <button onClick={onClose}>Cancel</button>
                           <button>
                             Delete
                           </button>
                           </form>
                         </div>
                       );
                     }
                   });
                  }}>Delete</button></td>

                {/* <td id="listedonne">{item.nom} &nbsp; {item.Prenom} 

                </td><br/> */}
              </div>
            );
          })}
       </tr>
      ) : (
        <div>
          <h2>Aucune enregistrement</h2>
        </div>
      )
    }
    </tbody>
    </table>
    </div>
  );
}
}

export default List;
